<?phpclass Validator {	const VALIDCHARS = '[a-zA-Z0-9-\._~:/\?\#@!%$&\'\(\)\*\+\,\;\=]';	static function isURLIPv6($string) {		return preg_match("#(^|[\n ])((https?|ftp)?://\[[a-fA-F0-9:\.]*\]".Validator::VALIDCHARS."*$)#is", $string);	}		static function isURL($string) {		if (preg_match("#(^|[\n ])((https?|ftp)?://".Validator::VALIDCHARS."+$)#is", $string)) {			return true;		}		if (preg_match("#(^|[\n ])((www|ftp)\.".Validator::VALIDCHARS."+\.[\w\-.\~]+$)#is", $string)) {			return true;		}		if (preg_match("#(^|[\n ])(mailto:[a-zA-Z0-9_]".Validator::VALIDCHARS."+$)#is", $string)) {			return true;		}		if (Validator::isURLIPv6($string)) {			return true;		}		return false;	}		static function isEMail($string) {		return preg_match('#(^|[\n ])([a-z0-9&\-_.]+?)@([\w\-]+\.([\w\-\.]+\.)*[\w]+)#i', $string);	}		static function isFilename($string) {		return preg_match('#^[^/?*:<>\\\n\t\r]+$#i', $string);	}}?>